    // View methods
    //

    /**
     * @dev See {IERC165-supportsInterface}.
     */
    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(IComputeManager, ERC165)
        returns (bool)
    {
        return interfaceId == type(IComputeManager).interfaceId || ERC165.supportsInterface(interfaceId);
    }
}
