pragma solidity 0.8.17;

import "./IPayment.sol";

interface IComputeRequestManager is IPayment {
    enum RequestStatus {
        OPEN,
        CLOSED,
        REVOKED
    }
    struct ComputeRequest {
        RequestStatus status;
        address requester;
        address computeManager;
        uint256 optionId;
        uint64 startTime;
        uint64 computeUnits;
        uint256 escrowAmount;
    }

    event ComputeRequested(
        address indexed requester,
        address indexed computeManager,
        uint256 optionId,
        uint256 computeId,
        uint256 computeUnits,
        bytes32 computeHash
    );
    event ComputeCompleted(address indexed requester, address indexed computeManager, uint256 computeId);
    event ComputeRevoked(address indexed requester, address indexed computeManager, uint256 computeId);

    /**
     * Request for compute.
     * @param computeManager The address of the compute manager contract.
     * @param optionId The given compute option index.
     * @param units The number of compute units to run.
     * @return computeId The id of the resulting compute record.
     * @param computeHash A compute manager identifier for this compute request.
     * @notice This contract must be authorised to spend ASTO on behalf of the caller.
     */
    function requestCompute(
        address computeManager,
        uint256 optionId,
        uint64 units,
        bytes32 computeHash
    ) external returns (uint256 computeId);

    /**
     * Revoke a compute request that has exceeded the SLA.
     * @param computeId The id of the compute record.
     * @notice This returns ASTO held in escrow.
     * @notice The compute request must not be completed and the SLA must have expired.
     */
    function revokeCompute(uint256 computeId) external;

    /**
     * Complete a compute request.
     * @param computeId The id of the compute record.
     * @notice This unlocks ASTO held in escrow.
     * @dev This must be called by the compute manager.
     */
    function completeCompute(uint256 computeId) external;

    function supportsInterface(bytes4 interfaceId) external returns (bool);
}

// SPDX-License-Identifier: MIT

pragma solidity 0.8.17;

interface IMemoryTree {
    struct MemoryTreeDetails {
        address brainAddress;
        uint256 brainId;
        uint256[] rootNodes;
    }
    struct MemoryNode {
        uint256 memoryTreeId;
        uint256[] children;
        string storageURI;
    }

    event MemoryNodeAdded(address brainAddress, uint256 brainId, uint256 nodeId, bytes32 nodeHash);

    /**
     * Add a root node to create a new memory tree for a given brain.
     * @param brainAddress The address of the brain contract.
     * @param brainId The token id for the brain.
     * @param nodeHash A compute manager identifier for this node.
     * @param storageURI The storage URI for the new node.
     * @param signature Function parameters hashed and signed by the signer.
     * @return treeId The new tree id.
     * @return nodeId The new node id.
     */
    function addMemoryTree(
        address brainAddress,
        uint256 brainId,
        bytes32 nodeHash,
        string memory storageURI,
        bytes calldata signature
    ) external returns (uint256 treeId, uint256 nodeId);

    /**
     * Add a node to the memory tree for a given brain.
     * @param parentNodeId The parent node for the new node.
     * @param nodeHash A compute manager identifier for this node.
     * @param storageURI The storage URI for the new node.
     * @param signature Function parameters hashed and signed by the signer.
     * @return nodeId The new node id.
     */
    function addNode(
        uint256 parentNodeId,
        bytes32 nodeHash,
        string memory storageURI,
        bytes calldata signature
    ) external returns (uint256 nodeId);

    /**
     * Returns the signer that validates requests.
     * @return signer The signing address.
     */
    function getSigner() external returns (address signer);

    /**
     * Get memory tree details.
     * @param index The index of the memory tree details to return.
     * @return details The memory tree details.
     */
    function getMemoryTreeDetails(uint256 index) external view returns (MemoryTreeDetails memory details);

    /**
     * Get memory tree node.
     * @param index The index of the memory tree node to return.
     * @return node The memory tree node.
     */
    function getMemoryNode(uint256 index) external view returns (MemoryNode memory node);

    function supportsInterface(bytes4 interfaceId) external returns (bool);
}

// SPDX-License-Identifier: MIT

pragma solidity 0.8.17;

interface IPayment {
    event AccountBalanceCorrected(address indexed account, uint256 amount);
    event DaoFeeUpdated(uint256 minimumFee, uint24 percentageFee);

    /**
     * Claim unlocked ASTO for a claimee.
     * @param to The address to send the claimed ASTO to.
     * @param amount The amount of ASTO to claim.
     * @notice After compute is completed, ASTO is unlocked for claiming.
     * @notice Caller may claim their unlocked ASTO with this method.
     */
    function claimAsto(address to, uint256 amount) external;

    /**
     * Stake ASTO to be used for payments.
     * @param to The address to recieve the staked amount.
     * @param amount The amount of ASTO to stake.
     * @notice Staked ASTO will be used to pay for compute requests.
     * @notice Caller may claim their staked ASTO at any time.
     * @dev This ASTO is added to the callers unlocked balance.
     */
    function stakeAsto(address to, uint256 amount) external;
}

